fun sumTransactionAmounts(): Float {
    // Start accumulation with BigDecimal.ZERO
    val sum = getMockedTransactions().fold(BigDecimal.ZERO) { acc, transaction ->
        // Add each transaction amount converted to BigDecimal
        acc + BigDecimal(transaction.amount.toDouble().toString())
    }
    // Set scale to 2 decimal places with HALF_EVEN rounding mode, and convert the result to Float
    return sum.setScale(2, RoundingMode.HALF_EVEN).toFloat()

}
